		<title>МЕТАФИЗИЧЕСКИЙ ОПЫТ БЫТИЯ (начало статьи)</title>
		<link>https://www.yuchikurov.info/%d0%bc%d0%b5%d1%82%d0%b0%d1%84%d0%b8%d0%b7%d0%b8%d1%87%d0%b5%d1%81%d0%ba%d0%b8%d0%b9-%d0%be%d0%bf%d1%8b%d1%82-%d0%b1%d1%8b%d1%82%d0%b8%d1%8f-%d0%bd%d0%b0%d1%87%d0%b0%d0%bb%d0%be-%d1%81%d1%82%d0%b0/</link>
		<comments>https://www.yuchikurov.info/%d0%bc%d0%b5%d1%82%d0%b0%d1%84%d0%b8%d0%b7%d0%b8%d1%87%d0%b5%d1%81%d0%ba%d0%b8%d0%b9-%d0%be%d0%bf%d1%8b%d1%82-%d0%b1%d1%8b%d1%82%d0%b8%d1%8f-%d0%bd%d0%b0%d1%87%d0%b0%d0%bb%d0%be-%d1%81%d1%82%d0%b0/#comments</comments>
